import React, {useEffect, useState} from 'react';
import './Category.css';
import testData from '../Assets/test.json';
import brands from '../Assets/brands.json';
import cats from '../Assets/categories.json';

const Category = () => {
    const prices = testData.map(item => item.price);
    const minDataPrice = Math.min(...prices);
    const maxDataPrice = Math.max(...prices);

    const [minPrice, setMinPrice] = useState(minDataPrice);
    const [maxPrice, setMaxPrice] = useState(maxDataPrice);

    useEffect(() => {
        setMinPrice(minDataPrice);
        setMaxPrice(maxDataPrice);
    }, [minDataPrice, maxDataPrice]);

    return (
        <div className='category-container'>
            <div className="filter">Filter</div>
            <div className="cat type">
                <label htmlFor="">Kategori</label>
                <select name="type-menu" id="">
                    <option key='' value='' selected disabled>Velg en kategori</option>
                    {cats.map((item, index) => (
                        <option key={index} value={item.name}>{item.name}</option>
                    ))}
                </select>
            </div>
            <div className="cat brand">
                <label htmlFor="">Merke</label>
                <select name="brand-menu" id="">
                    <option key='' value='' selected disabled>Velg et merke</option>
                    {brands.map((item, index) => (
                        <option key={index} value={item.name}>{item.name}</option>
                    ))}
                </select>
            </div>
            <div className="cat min-price">
                <label htmlFor="">Min price: {parseInt(minPrice)}</label>
                <input
                    type="range"
                    step={10}
                    min={minDataPrice}
                    max={maxDataPrice}
                    value={minPrice}
                    onChange={(e) => setMinPrice(Number(e.target.value))}
                />
            </div>
            <div className="cat max-price">
                <label htmlFor="">Max price: {parseInt(maxPrice)}</label>
                <input
                    type="range"
                    step={10}
                    min={minDataPrice}
                    max={maxDataPrice}
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(Number(e.target.value))}
                />
            </div>
        </div>
    )
}

export default Category;
