import React, { useState } from 'react';
import './Category.css';
import testData from '../Assets/test.json';
import brands from '../Assets/brands.json';
import cats from '../Assets/categories.json';
import Button from '../Button/Button';

const Category = () => {
    const priceList = testData.map(item => item.price);
    const minDataPrice = Math.min(...priceList);
    const maxDataPrice = Math.max(...priceList);

    const [minPrice, setMinPrice] = useState(() => minDataPrice);
    const [maxPrice, setMaxPrice] = useState(() => maxDataPrice);

    return (
        <div className='category-container'>
            <h2 className='filter'>
                <div className='filter-icon'>≓</div>Filter
            </h2>

            <div className="cat type">
                <label htmlFor="select-category"><h3>Kategori</h3></label>
                <select name="type-menu" id="select-category" defaultValue="">
                    <option value="">Velg en kategori</option>
                    {cats.map((item) => (
                        <option key={item.name} value={item.name}>
                            {item.name}
                        </option>
                    ))}
                </select>
            </div>

            <div className="cat brand">
                <label htmlFor="select-brand"><h3>Merke</h3></label>
                <select name="brand-menu" id="select-brand" defaultValue="">
                    <option value="">Velg et merke</option>
                    {brands.map((item) => (
                        <option key={item.name} value={item.name}>
                            {item.name}
                        </option>
                    ))}
                </select>
            </div>

            <div className="cat min-price">
                <label><h3>Min price: {minPrice}</h3></label>
                <input
                    type="range"
                    step={10}
                    min={minDataPrice}
                    max={maxDataPrice}
                    value={minPrice}
                    onChange={(e) => setMinPrice(Number(e.target.value))}
                />
            </div>

            <div className="cat max-price">
                <label><h3>Max price: {maxPrice}</h3></label>
                <input
                    type="range"
                    step={10}
                    min={minDataPrice}
                    max={maxDataPrice}
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(Number(e.target.value))}
                />
            </div>

            <div className="cat apply-filter">
                <Button type='light' text='Apply filter →' />
            </div>
        </div>
    );
};

export default Category;
