import React from 'react';
import './ItemList.css';
import testData from "../Assets/test.json";
import ProductItem from "../ProductItem/ProductItem";
import { FixedSizeGrid as Grid } from 'react-window';

const columnCount = 3; // Adjust based on your responsive needs

const ItemList = () => {
    const columnWidth = 300; // Adjust to match minmax(280px) in CSS
    const rowHeight = 375; // Aspect ratio 4/5 of 300px width

    const rowCount = Math.ceil(testData.length / columnCount);

    const Cell = ({ columnIndex, rowIndex, style }) => {
        const index = rowIndex * columnCount + columnIndex;
        if (index >= testData.length) return null;

        const item = testData[index];
        return (
            <div className="item-wrapper" style={style}>
                <ProductItem
                    image={item.image}
                    name={item.name}
                    price={parseInt(item.price)}
                    quantity={item.quantity}
                />
            </div>
        );
    };

    return (
        <div className='cont'>
            <div className='info'>ElectroMart - Products</div>
            <div className="item-list-container">
                <Grid
                    columnCount={columnCount}
                    columnWidth={columnWidth}
                    height={600} // adjust as needed
                    rowCount={rowCount}
                    rowHeight={rowHeight}
                    width={columnWidth * columnCount}
                >
                    {Cell}
                </Grid>
            </div>
        </div>
    );
};

export default ItemList;
