import React, { useState } from 'react';
import './ItemList.css';
import testData from "../Assets/test.json";
import ProductItem from "../ProductItem/ProductItem";

export default function ItemList() {
    // Number of items to show per page
    const itemsPerPage = 3;

    // State for current page index (1-based)
    const [currentPage, setCurrentPage] = useState(1);

    // Calculate total pages
    const totalPages = Math.ceil(testData.length / itemsPerPage);

    // Determine start/end indices for slicing data
    const startIdx = (currentPage - 1) * itemsPerPage;
    const endIdx = startIdx + itemsPerPage;
    const currentItems = testData.slice(startIdx, endIdx);

    // Handlers for pagination controls
    const goToPage = (page) => {
        const pageNumber = Math.max(1, Math.min(page, totalPages));
        setCurrentPage(pageNumber);
    };

    const handlePrev = () => goToPage(currentPage - 1);
    const handleNext = () => goToPage(currentPage + 1);

    return (
        <div className="cont">
            <div className="info">ElectroMart - Products</div>

            <div className="item-list-container">
                {currentItems.map((item, index) => (
                    <div className="item-wrapper" key={startIdx + index}>
                        <ProductItem
                            image={item.image}
                            name={item.name}
                            price={parseInt(item.price)}
                            quantity={item.quantity}
                        />
                    </div>
                ))}
            </div>

            {/* Pagination controls */}
            {totalPages > 1 && (
                <div className="pagination">
                    <button onClick={handlePrev} disabled={currentPage === 1}>
                        Prev
                    </button>

                    {Array.from({ length: totalPages }, (_, idx) => (
                        <button
                            key={idx + 1}
                            onClick={() => goToPage(idx + 1)}
                            className={currentPage === idx + 1 ? 'active' : ''}
                        >
                            {idx + 1}
                        </button>
                    ))}

                    <button onClick={handleNext} disabled={currentPage === totalPages}>
                        Next
                    </button>
                </div>
            )}
        </div>
    );
}
