import React from 'react';
import './ItemList.css';
import testData from "../Assets/test.json";
import ProductItem from "../ProductItem/ProductItem";
import { FixedSizeGrid as Grid } from 'react-window';
import AutoSizer from 'react-virtualized-auto-sizer';

const ItemList = () => {
    const itemMinWidth = 280;
    const itemAspectRatio = 4 / 5;

    const Cell = ({ columnIndex, rowIndex, style, data }) => {
        const { items, columnCount } = data;
        const index = rowIndex * columnCount + columnIndex;
        if (index >= items.length) return null;

        const item = items[index];

        return (
            <div className="item-wrapper" style={style}>
                <ProductItem
                    image={item.image}
                    name={item.name}
                    price={parseInt(item.price)}
                    quantity={item.quantity}
                />
            </div>
        );
    };

    return (
        <div className='cont'>
            <div className='info'>ElectroMart - Products</div>
            <div className="item-list-container">
                <AutoSizer>
                    {({ width, height }) => {
                        const columnCount = Math.floor(width / itemMinWidth) || 1;
                        const columnWidth = Math.floor(width / columnCount);
                        const rowHeight = Math.floor(columnWidth / itemAspectRatio);
                        const rowCount = Math.ceil(testData.length / columnCount);

                        return (
                            <Grid
                                columnCount={columnCount}
                                columnWidth={columnWidth}
                                height={height}
                                rowCount={rowCount}
                                rowHeight={rowHeight}
                                width={width}
                                itemData={{ items: testData, columnCount }}
                            >
                                {Cell}
                            </Grid>
                        );
                    }}
                </AutoSizer>
            </div>
        </div>
    );
};

export default ItemList;
